import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchPuzzles } from '../store/puzzleSlice';
import './PuzzleList.css';

const PuzzleList = ({ onSelectPuzzle }) => {
  const dispatch = useDispatch();
  const { puzzles, loading } = useSelector(state => state.puzzle);

  useEffect(() => {
    dispatch(fetchPuzzles());
  }, [dispatch]);

  if (loading) return <div>Loading puzzles...</div>;

  return (
    <div className="puzzle-list">
      <h2>Available Puzzles</h2>
      {puzzles.length === 0 ? (
        <p>No puzzles available. Create one to get started!</p>
      ) : (
        <div className="puzzle-cards">
          {puzzles.map(puzzle => (
            <div 
              key={puzzle.id} 
              className={`puzzle-card ${puzzle.solved ? 'solved' : ''}`}
              onClick={() => onSelectPuzzle(puzzle.id)}
            >
              <h3>{puzzle.name}</h3>
              <p className="puzzle-status">
                {puzzle.solved ? 'âœ… Solved' : 'ðŸ§© Unsolved'}
              </p>
              <p className="puzzle-date">
                Created: {new Date(puzzle.createdAt).toLocaleDateString()}
              </p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default PuzzleList;
