import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchPuzzles } from '../store/puzzleSlice';
import './PuzzleList.css';

const PuzzleList = ({ onSelectPuzzle }) => {
  const dispatch = useDispatch();
  const { puzzles, loading } = useSelector(state => state.puzzle);

  const handleDeletePuzzle = async (puzzleId, e) => {
    e.stopPropagation();
    if (window.confirm('Are you sure you want to delete this puzzle?')) {
      try {
        await fetch(`/api/puzzles/${puzzleId}`, { method: 'DELETE' });
        dispatch(fetchPuzzles());
      } catch (error) {
        console.error('Failed to delete puzzle:', error);
      }
    }
  };

  const handleResetPuzzle = async (puzzleId, e) => {
    e.stopPropagation();
    if (window.confirm('Reset this puzzle to unsolved state?')) {
      try {
        const response = await fetch(`/api/puzzles/${puzzleId}/reset`, { 
          method: 'POST'
        });
        if (response.ok) {
          dispatch(fetchPuzzles());
        } else {
          console.error('Reset failed:', response.status);
        }
      } catch (error) {
        console.error('Failed to reset puzzle:', error);
      }
    }
  };

  useEffect(() => {
    dispatch(fetchPuzzles());
  }, [dispatch]);

  if (loading) {
    return <div className="loading">Loading puzzles...</div>;
  }

  return (
    <div className="puzzle-list">
      <h2>Puzzle Collection</h2>
      {puzzles.length === 0 ? (
        <div className="no-puzzles">
          <p>No puzzles yet!</p>
          <p>Create your first puzzle to get started!</p>
        </div>
      ) : (
        <div className="puzzle-cards">
          {puzzles.map(puzzle => (
            <div 
              key={puzzle.id} 
              className={`puzzle-card ${puzzle.solved ? 'completed' : ''}`}
              onClick={() => onSelectPuzzle(puzzle.id)}
            >
              <h3>{puzzle.name}</h3>
              <div className={`puzzle-status ${puzzle.solved ? 'solved' : 'unsolved'}`}>
                {puzzle.solved ? '‚úÖ Completed' : 'üéØ Ready to solve'}
              </div>
              <div className="puzzle-date">
                Created: {new Date(puzzle.createdAt).toLocaleDateString()}
              </div>
              <div className="puzzle-actions">
                <button 
                  className="reset-btn"
                  onClick={(e) => handleResetPuzzle(puzzle.id, e)}
                  title="Reset puzzle"
                >
                  ‚Ü©Ô∏è
                </button>
                <button 
                  className="delete-btn"
                  onClick={(e) => handleDeletePuzzle(puzzle.id, e)}
                  title="Delete puzzle"
                >
                  üóëÔ∏è
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default PuzzleList;
